let questionNumber = 1

function renderAdvice(src, text) {
    return `
        <img 
        src="../assets/image/${src}.jpg" 
        alt="–ö–∞—Ä—Ç–∏–Ω–∫–∞ —Å–æ–≤–µ—Ç–∞" 
        class="menu__advice-image"
        width="180"
        srcset="../assets/image/${src}.jpg 1x, ../assets/image/@2x/${src}@2x.jpg 2x, ../assets/image/@3x/${src}@3x.jpg 3x"
        />
        <p class="menu__advice-text">${text}</p>
        <div class="menu__button-wrapper"> 
            <button class="menu__button" onclick="renderQuestion()">–î–∞–ª—å—à–µ</button>
        </div>
    `
}

function scoreIncrement() {
    for (let i = 1; i < 6; i++) {
        setTimeout(() => {
            score.textContent = Number(score.textContent) + 1
        }, i * 100)
    }
}

function renderConclusion() {
    if (Number(score.textContent) % 5 === 0) {
        const finalScore = score.textContent
        const questionsPassed = Number(finalScore) / 5
        const advice = questionsPassed > 5
        ? '–û—Ç–ª–∏—á–Ω–æ! –£ –≤–∞—Å —Ö–æ—Ä–æ—à–∏–µ –∑–Ω–∞–Ω–∏—è –≤ –æ–±–ª–∞—Å—Ç–∏ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç-–±–µ–∑–æ–ø–∞—Å–Ω–æ—Å—Ç–∏. –ü—Ä–æ–¥–æ–ª–∂–∞–π—Ç–µ –±—ã—Ç—å –±–¥–∏—Ç–µ–ª—å–Ω—ã–º–∏ –≤ —Å–µ—Ç–∏, –∏ –≤—ã –±—É–¥–µ—Ç–µ –≤ –±–µ–∑–æ–ø–∞—Å–Ω–æ—Å—Ç–∏!ü§ù'
        : '–ü–æ—Ö–æ–∂–µ –≤—ã —Ç–æ–ª—å–∫–æ –Ω–∞—á–∏–Ω–∞–µ—Ç–µ —Å–≤–æ–π –ø—É—Ç—å –∫ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç-–≥—Ä–∞–º–æ—Ç–Ω–æ—Å—Ç–∏. –ü–æ–º–Ω–∏—Ç–µ –∫–∞–∂–¥—ã–π —à–∞–≥ –∫ –ø–æ–≤—ã—à–µ–Ω–∏—é —Å–≤–æ–µ–π –∏–Ω—Ç–µ—Ä–Ω–µ—Ç-–æ—Å–≤–µ–¥–æ–º–ª–µ–Ω–Ω–æ—Å—Ç–∏ ‚Äî —ç—Ç–æ —É–∂–µ –ø–æ–±–µ–¥–∞!üòâ'
        menu.style.boxShadow = questionsPassed > 5 ? 'var(--grin-shadow)' : 'var(--red-shadow)'
        menu.innerHTML = `
                <h3 class="conclusion">–í–∞—à —Ä–µ–∑—É–ª—å—Ç–∞—Ç:</h3>
                <span class="menu__questions-passed">${questionsPassed}/10</span>
                <span class="menu__final-score">–ë–∞–ª–ª–æ–≤: <span id="score">${finalScore}</span></span>
                <p class="menu__final-advice" id="final-advice">${advice}</p>
                <div class="menu__button-wrapper">
                    <a href="https://akudera.github.io/Security-Testing/questions/index.html" class="menu__button">–ü—Ä–æ–π—Ç–∏ —Ç–µ—Å—Ç –µ—â–µ —Ä–∞–∑</a>
                </div>
                `
    } else {
        setTimeout(renderConclusion, 100)
    }
}

function nextQuestion(answerId) {
    const question = questions[questionNumber]
    if (!question) return

    const selectedAnswer = question.options[answerId - 1]
    if (!selectedAnswer) {
        console.error('–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –≤—ã–±–æ—Ä –æ—Ç–≤–µ—Ç–∞!')
        return
    }

    const adviceImage = selectedAnswer.isCorrect ? 'feather' : 'breakFeather'
    menu.style.boxShadow = selectedAnswer.isCorrect ? 'var(--grin-shadow)' : 'var(--red-shadow)'
    if (selectedAnswer.isCorrect) {
        scoreIncrement()
        scoreCounter.style.display = 'inline'
    }
    
    questionContainer.innerHTML = renderAdvice(adviceImage, selectedAnswer.advice)
    questionNumber++
}

function renderQuestion() {
    const question = questions[questionNumber]
    if (question) {
        questionContainer.innerHTML = menuEl
        menu.style.boxShadow = 'var(--blue-shadow)'
        scoreCounter.style.display = 'none'

        document.getElementById('question-count').textContent = questionNumber
        document.getElementById('question').textContent = question.text
        const questionImageEl = document.getElementById('image')
        questionImageEl.src = `../assets/image/${question.image}.jpg`
        questionImageEl.srcset = `../assets/image/${question.image}.jpg 1x, ../assets/image/@2x/${question.image}@2x.jpg 2x, ../assets/image/@3x/${question.image}@3x.jpg 3x`
        questionImageEl.setAttribute('width', 200)
        questionImageEl.style.aspectRatio = '1/1'

        question.options.forEach((option, index) => {
            document.getElementById(`${index + 1}`).textContent = option.text
        })
    } else {
        renderConclusion()
    }
}

renderQuestion()